import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

const Dashboard = () => {
    const chartRef = useRef(null);

    useEffect(() => {
        const ctx = chartRef.current.getContext('2d');
        new Chart(ctx, {
            type: 'line',
            data: {
                labels: ['Day 1', 'Day 5', 'Day 10', 'Day 15', 'Day 20', 'Day 25', 'Day 30'],
                datasets: [{
                    label: 'Sales',
                    data: [12, 19, 3, 5, 2, 3, 20],
                    backgroundColor: 'rgba(54, 162, 235, 0.2)',
                    borderColor: 'rgba(54, 162, 235, 1)',
                    borderWidth: 1
                }]
            },
            options: {
                scales: {
                    y: {
                        beginAtZero: true
                    }
                },
                plugins: {
                    tooltip: {
                        enabled: true
                    }
                },
                onClick: (event, elements) => {
                    if (elements.length > 0) {
                        const index = elements[0].index;
                        const label = chartRef.current.chart.data.labels[index];
                        const value = chartRef.current.chart.data.datasets[0].data[index];
                        alert(`Clicked on ${label}: ${value}`);
                    }
                }
            }
        });
        chartRef.current.chart = chartRef.current.chart;
    }, []);

    return (
        <div className="row">
            <div className="col-md-4">
                <div className="card shadow-sm border-0">
                    <div className="card-body">
                        <h5 className="card-title"><i className="fas fa-shopping-cart me-2"></i> Orders Placed</h5>
                        <p className="card-text display-4">125</p>
                    </div>
                </div>
            </div>
            <div className="col-md-4">
                <div className="card shadow-sm border-0">
                    <div className="card-body">
                        <h5 className="card-title"><i className="fas fa-question-circle me-2"></i> Queries Received</h5>
                        <p className="card-text display-4">57</p>
                    </div>
                </div>
            </div>
            <div className="col-md-12 mt-4">
                <div className="card shadow-sm border-0">
                    <div className="card-body">
                        <h5 className="card-title"><i className="fas fa-chart-line me-2"></i> Sales (Last 30 Days)</h5>
                        <canvas ref={chartRef} width="400" height="200"></canvas>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
